    // ) => {
    //   // state.token = "sdadsadasdasdsa";
    //   localStorage.setItem("token", "");
    // },
    // register: () => {},
  },
});

// export const { login, register } = usersSlice.actions;
export const { overrideStateProps } = usersSlice.actions;
export default usersSlice.reducer;
