// import { useSelector, useDispatch, connect } from "react-redux";
// import { AppDispatch, RootState } from "@/store"; // 用下面的替换
import { Form, Input, Checkbox, Button, Row, Col } from "antd";
import { connect, ConnectedProps } from "react-redux";
import { useAppSelector, useAppDispatch } from "@/hook";
import { RouteConfig } from "react-router-config";
import { User } from "@/interfaces/login";
import { increment, decrement, incrementByAmount } from "@/reducers/counter";
import { overrideStateProps } from "@/reducers/login";
// interface RootState {
//   counter: number;
//   login: User;
// }

const connector = () => {
  const mapState = (state: any) => ({
    counter: state.counter,
    login: state.login,
  });
  // 对象形式
  const mapDispatch = {
    increment: () => increment(),
    decrement: () => decrement(),
    incrementByAmount: (num: number) => incrementByAmount(num),
    update: (payload: any) => overrideStateProps(payload),
  };

  // 函数形式
  // const mapDispatch = (dispatch: any) => ({
  //   increment: () => {
  //     dispatch(increment());
  //   },
  //   decrement: () => {
  //     dispatch(decrement());
  //   },
  //   incrementByAmount: (num: number) => {
  //     dispatch(incrementByAmount(num));
  //   },
  //   update: (payload: any) => {
  //     console.log(payload);
  //     dispatch(overrideStateProps(payload));
  //   },
  // });
  return connect(mapState, mapDispatch);
};

type PropsFromRedux = ConnectedProps<typeof connector>;
type Props = PropsFromRedux & RouteConfig;
function Login(props: Props) {
  console.log(props);
  // const count = useAppSelector(state => state.counter);
  // const dispatch = useAppDispatch();

  function login() {
    // history.push("/");
  }
  const onFinish = (values: any) => {
    console.log("Success:", values);
  };

  const onFinishFailed = (errorInfo: any) => {
    console.log("Failed:", errorInfo);
  };
  return (
    <div
      style={{
        display: "flex",
        flexFlow: "column nowrap",
        justifyContent: "center",
      }}
    >
      <Row>
        <Col span={12} offset={6}>
          <Form
            name="basic"
            labelCol={{ span: 3 }}
            wrapperCol={{ span: 16 }}
            initialValues={{ remember: true }}
            onFinish={onFinish}
            onFinishFailed={onFinishFailed}
          >
            <Form.Item
              label="Username"
              rules={[
                { required: true, message: "Please input your username!" },
              ]}
            >
              <Input
                onChange={e => props.update({ username: e.target.value })}
              />
              {props.login.username}
            </Form.Item>

            <Form.Item
              label="Password"
              rules={[
                { required: true, message: "Please input your password!" },
              ]}
            >
              <Input.Password
                onChange={e => props.update({ password: e.target.value })}
              />
              {props.login.password}
            </Form.Item>

            <Form.Item
              name="remember"
              valuePropName="checked"
              wrapperCol={{ offset: 8, span: 16 }}
            >
              <Checkbox>Remember me</Checkbox>
            </Form.Item>

            <Form.Item wrapperCol={{ offset: 8, span: 16 }}>
              <Button type="primary" htmlType="submit">
                Submit
              </Button>
            </Form.Item>
          </Form>
        </Col>
      </Row>
      <Row>
        {/* <button onClick={() => dispatch(increment())}>+</button> */}
        <button onClick={props.increment}>+</button>
        {props.counter.value}
        <button onClick={props.decrement}>-</button>
        <button onClick={() => props.incrementByAmount(5)}>+5</button>
        {/* <button onClick={() => dispatch(incrementByAmount(5))}>+5</button> */}
      </Row>
    </div>
  );
}

export default connector(Login);
// export default Login;
