import React from "react";
import { Switch, Route, Redirect } from "react-router";

interface RouteConfig {
  key?: string;
  path: string;
  exact?: boolean;
  component?: any;
  render?: any;
  routes: [];
}
type Props = RouteConfig[];

// 高阶组件 可以添加路由守卫
const connect = function (route: RouteConfig) {
  return function (WrappedComponent: any) {
    return class extends React.Component {
      constructor(props: any) {
        super(props);
      }
      render() {
        debugger;
        return <WrappedComponent route={route} {...this.props} />;
      }
    };
  };
};

export default function RenderRoutes(routes: Props | undefined) {
  return (
    <Switch>
      {routes?.map((route: RouteConfig) => (
        <Route
          key={route.path?.toString()}
          path={`${route.path}`}
          exact={route.exact}
          component={connect(route)(route.component)}
          render={route.render}
        />
      ))}
      {/* <Redirect from="/home" to="/home/overall/national-trend" /> */}
    </Switch>
  );
}
