import { createSlice, PayloadAction, createAsyncThunk } from "@reduxjs/toolkit";
import {
  login as loginService,
  register as registerService,
} from "@/services/user";
import { User } from "@/interfaces/user";
import { message } from "antd";
const NAMESPACE = "login";
const initialState = {
  token: "",
  user: "",
  // username: "",
  // password: "",
};

export const login = createAsyncThunk(
  `${NAMESPACE}/login`,
  async (params: User) => {
    // console.log(params);
    const response = await loginService(params);
    return response;
  }
);

export const register = createAsyncThunk(
  `${NAMESPACE}/register`,
  async (params: User) => {
    const response = await registerService(params);
    return response;
  }
);
const usersSlice = createSlice({
  name: NAMESPACE,
  initialState,
  reducers: {
    overrideStateProps(state, { payload }: PayloadAction<User>) {
      return {
        ...state,
        ...payload,
      };
    },
    // getUser: state => {
    //   state.username = "admin";
    // },
    // login: (
    //   state,
    //   action: PayloadAction<{ name: string; password: string }>
    // ) => {
    //   // state.token = "sdadsadasdasdsa";
    //   localStorage.setItem("token", "");
    // },
    // register: () => {},
  },
  // 处理异步请求结果的reducer
  extraReducers(builder) {
    builder
      .addCase(login.pending, (state, action) => {
        // state.status = 'loading'
      })
      .addCase(login.fulfilled, (state, action) => {
        const { payload: any } = action;
        const { status, data, msg } = payload;
        if (status == 200) {
          message.info("登录成功");
        } else {
          message.error(msg);
        }
        // console.log(payload);
        // state.status = 'succeeded'
        // // Add any fetched posts to the array
        // state.posts = state.posts.concat(action.payload)
      })
      .addCase(login.rejected, (state, action) => {
        // state.status = 'failed'
        // state.error = action.error.message
      })
      .addCase(register.fulfilled, (state, action) => {});
  },
});

// export const { login, register } = usersSlice.actions;
export const { overrideStateProps } = usersSlice.actions;
export default usersSlice.reducer;
