import request from "@/utils/request";

import { User } from "@/interfaces/login";
// interface User {
//   username: string;
//   password: string;
// }
export async function login(params: User) {
  // return request("/login", {
  //   method: "POST",
  //   data: params,
  // });
  return request("/", {
    method: "GET",
    data: params,
  });
}

export async function register(params: User) {
  return request("/register", {
    data: params,
  });
}
