import { createSlice, PayloadAction, createAsyncThunk } from "@reduxjs/toolkit";
import {
  login as loginService,
  register as registerService,
} from "@/services/login";

// declare interface UsersState {
//   name: string;
//   token: number;
// }

import { User } from "@/interfaces/login";
const NAMESPACE = "login";
const initialState: User = {
  username: "",
  password: "",
};

export const login = createAsyncThunk(`${NAMESPACE}/login`, async state => {
  // console.log(state);
  const response = await loginService({ username: "", password: "" });
  // console.log(response);
  return response;
});

const usersSlice = createSlice({
  name: NAMESPACE,
  initialState,
  reducers: {
    overrideStateProps(state, { payload }: PayloadAction<User>) {
      return {
        ...state,
        ...payload,
      };
    },
    // getUser: state => {
    //   state.username = "admin";
    // },
    // login: (
    //   state,
    //   action: PayloadAction<{ name: string; password: string }>
    // ) => {
    //   // state.token = "sdadsadasdasdsa";
    //   localStorage.setItem("token", "");
    // },
    // register: () => {},
  },
  // 处理异步请求结果的reducer
  extraReducers(builder) {
    builder
      .addCase(login.pending, (state, action) => {
        // state.status = 'loading'
      })
      .addCase(login.fulfilled, (state, action) => {
        const { payload } = action;
        console.log(payload);
        // state.status = 'succeeded'
        // // Add any fetched posts to the array
        // state.posts = state.posts.concat(action.payload)
      })
      .addCase(login.rejected, (state, action) => {
        // state.status = 'failed'
        // state.error = action.error.message
      });
  },
});

// export const { login, register } = usersSlice.actions;
export const { overrideStateProps } = usersSlice.actions;
export default usersSlice.reducer;
