import React, { useMemo, useEffect } from "react";
import PropTypes from "prop-types";
import { Switch, Route, Redirect } from "react-router";

interface RedirectConfig {
  redirect: boolean;
  from?: string;
  to: string;
}
interface Meta {
  requireAuth?: boolean;
}
interface RouteConfig {
  key?: string;
  path: string;
  exact?: boolean;
  component?: any;
  render?: any;
  routes: IRoute[];
  meta?: any;
}

type IRoute = RouteConfig | RedirectConfig;
type Props = Array<IRoute>;

const beforeEach = function () {};

// 高阶组件 可以添加路由守卫
const connect = function (route: RouteConfig) {
  return function (WrappedComponent: any) {
    return class extends React.Component {
      constructor(props: any) {
        super(props);
        this.state = {};
      }
      beforeEach = (to: RouteConfig, from: RouteConfig) => {
        // if (to?.meta?.requireAuth && !auth.isLoggedIn()) {
        // }
      };
      componentDidMount() {
        console.log("mount");
      }
      render() {
        const token = localStorage.getItem("token");
        // 执行守卫
        beforeEach();
        if (token) {
          return <WrappedComponent route={route} {...this.props} />;
        } else {
        }
        // debugger;
      }
    };
  };
};

// const hocConnect = WrappedComponent => {
//   const hocComponent = ({ ...props }) => <WrappedComponent {...props} />;
//   hocComponent.propTypes = {};
//   return hocComponent;
// };

const RoutesComponent = React.memo(function (props: any) {
  const routes = props.routes;

  useEffect(() => {
    // effect
    return () => {
      // cleanup
    };
  }, []);

  return (
    <>
      {routes?.map((route: any, index: number) =>
        !route?.redirect ? (
          <Route
            key={route.path?.toString()}
            path={`${route.path}`}
            exact={route?.exact}
            component={
              // route.component ? connect(route)(route?.component) : undefined
              route.component ? route?.component : undefined
            }
            render={route?.render}
          />
        ) : (
          <Redirect key={"r" + index} from={route?.from} to={route?.to || ""} />
        )
      )}
    </>
  );
});

function RenderRoutes(props: any) {
  console.log(props);
  const routes = props?.routes;
  const location = props?.location;
  useEffect(() => {
    console.log(routes);
    return () => {
      // cleanup
    };
  }, []);
  return (
    <Switch>
      {/* <RoutesComponent routes={routes} /> */}
      {routes?.map((route: any, index: number) =>
        !route?.redirect ? (
          <Route
            key={route.path?.toString()}
            path={`${route.path}`}
            exact={route?.exact}
            component={
              // route.component ? connect(route)(route?.component) : undefined
              route.component ? route?.component : undefined
            }
            render={route?.render}
          />
        ) : (
          <Redirect key={"r" + index} from={route?.from} to={route?.to || ""} />
        )
      )}
    </Switch>
  );
}

function isEqual(prevProps: any, nextProps: any): boolean {
  /*
  如果把 nextProps 传入 render 方法的返回结果与
  将 prevProps 传入 render 方法的返回结果一致则返回 true，
  否则返回 false
  */
  return Object.is(prevProps.routes, nextProps.routes);
}
// export default React.memo(RenderRoutes, isEqual);
export default React.memo(RenderRoutes);
